import React from "react";
import { Renderer, Parser, Lexer } from "marked";
import {marked} from "marked"

const myComponent = () => {
  const textStyle = {
    fontFamily: "'Courier Prime', monospace",
    fontFamily: "'Lato', sans-serif",
  };
}

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      markdown: 
`# Heading
## Subheading
[Link](https://example.com)
Inline code: \`console.log('Hello, world!')\`
\`\`\`
// Code block
function greet(name) {
  return "Hello, " + name + "!";
}
\`\`\`
- List item 1
- List item 2
> Blockquote
![Image](https://example.com/image.jpg)
**Bold text**`,
    };
  }

  updateMarkdown(markdown) {
    this.setState({ markdown });
  }

  componentDidMount() {
    // Render default markdown in preview element
    const previewElement = document.getElementById("preview");
    previewElement.innerHTML = marked(this.state.markdown);
  }

  componentDidUpdate() {
    // Update preview element whenever markdown changes
    const previewElement = document.getElementById("preview");
    previewElement.innerHTML = marked(this.state.markdown);
  }

  render() {
    var inputStyle = {
      width: "40rem",
      height: "70vh",
      marginLeft: "auto",
      marginRight: "auto",
      padding: "10px",
      color: "#A87D19",
      backgroundColor: "black",
      border: "none",
      borderBottom: "2px solid black",
      position: "relative",
    };
    var outputStyle = {
      width: "35rem",
      height: "50vh",
      backgroundColor: "#DCDCDC",
      marginLeft: "auto",
      marginRight: "auto",
      padding: "10px",
      backgroundColor: "black",
    };
    var verticalBarStyle = {
      content: "''",
      position: "absolute",
      top: "-10px",
      bottom: "0",
      left: "-1px",
      width: "2px",
      backgroundColor: "#A87D19",
    };


    return (
      <div style={{backgroundColor: "black", fontFamily: "'Courier Prime', monospace", color: "#A87D19" }}>
      <div className="App">
        <div className="container">
          <div className="row mt-4">
            <div className="col text-center">
              <h1>Markdown Previewer</h1>
            </div>
          </div>

          <div className="row mt-4">
            <div className="col-md-6">
              <div className="col text-center">
              <h4>Markdown Input</h4>
              </div>
              <div style={{ display: "flex", justifyContent: "center" }}>
                <div className="input" style={inputStyle}>
                  <div style={verticalBarStyle}></div>
                <textarea
                  id="editor"
                  className="input"
                  style={inputStyle}
                  value={this.state.markdown}
                  onChange={(e) => {
                    this.updateMarkdown(e.target.value);
                  }}
                />
              </div>
            </div>
            </div>

            <div className="col-md-6">
              <div className="col text-center">
                <h4>Preview</h4>
              </div>
              <div
                id="preview"
                style={outputStyle}
                dangerouslySetInnerHTML={{
                  __html: marked(this.state.markdown, { renderer: new Renderer() }),
                }}
              />
            </div>
          </div>
        </div>
      </div>
      </div>
    );
  }
}
